    private void mostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mostrarActionPerformed
            String t = titulo.getText();
            String c = contenido.getText();
        if (stop.isSelected()){ 
            
            JOptionPane.showMessageDialog(null,c,t,JOptionPane.ERROR_MESSAGE);
        }
        if (interrogacion.isSelected()){
           
            JOptionPane.showMessageDialog(null,c,t,JOptionPane.QUESTION_MESSAGE);
        }
        if (exclamacion.isSelected()){
           
            JOptionPane.showMessageDialog(null,c,t,JOptionPane.WARNING_MESSAGE);
        }
        if (informacion.isSelected()){
           
            JOptionPane.showMessageDialog(null,c,t,JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_mostrarActionPerformed

