    private void afpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_afpActionPerformed
      if (afp.isSelected()){
          double sbt = Double.parseDouble(soubasictexto.getText());
          double a = sbt * 0.03;
          afptexto.setText(Double.toString(a));
      }
      else{
          afptexto.setText("0");
      }
    }//GEN-LAST:event_afpActionPerformed

