    private void RendaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RendaActionPerformed
        double r;
        double sbt;
        if (Renda.isSelected()){
            sbt = Double.parseDouble(soubasictexto.getText());
            r = sbt * 0.1;
            Rendatexto.setText(Double.toString(r));
        }
        else{
            Rendatexto.setText("0");
        }
    }//GEN-LAST:event_RendaActionPerformed

    private void FonaviActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FonaviActionPerformed
        if (Fonavi.isSelected()){
            double sbt = Double.parseDouble(soubasictexto.getText());
            double f = sbt *7/100;
            Fonavitexto.setText(Double.toString(f));
        }
        else{
            Fonavitexto.setText("0");
        } 
            
    }//GEN-LAST:event_FonaviActionPerformed

